export default class StepReport {
    readonly description: string;
    readonly message: string;
    readonly passed: boolean;
    readonly screenshot?: string | undefined;
    readonly guid: string;
    constructor(description: string, message: string, passed: boolean, screenshot?: string | undefined);
    toJson(): {
        guid: string;
        description: string;
        message: string;
        passed: boolean;
        screenshot?: string;
    };
}
